export default {
  name: "ContactPage",
  data() {
    return {
      formData: {
        name: '',
        email: '',
        message: ''
      }
    };
  },
  methods: {
    submitForm() {
      // フォームデータをサーバーに送信する処理
      // この例では、POSTリクエストを使用してPHPスクリプトにデータを送信します

      const url = '';
      // PHPスクリプトへのパスを設定する

      // フォームデータをPOSTリクエストのボディに変換
      const formData = new FormData();
      formData.append('name', this.formData.name);
      formData.append('email', this.formData.email);
      formData.append('message', this.formData.message);

      // POSTリクエストを送信
      fetch(url, {
        method: 'POST',
        body: formData
      }).then(response => response.json()).then(data => {
        // レスポンスを処理するためのコード
        console.log(data);
        // 成功メッセージを表示など、適宜処理を追加してください
      }).catch(error => {
        // エラーハンドリング
        console.error(error);
      });
    }
  }
};
