export default {
  name: "ContactPage",
  data() {
    return {
      users: [],
      userApi: "http://localhost/sneakers/rest/api/V1/users.php",
      userObj: {
        name: "",
        email: "",
        message: ""
      }
    };
  },
  methods: {
    async getUsers() {
      try {
        let response = await fetch(this.userApi);
        this.users = await response.json();
      } catch (error) {
        console.log(error);
      }
    },
    async postUsers() {
      try {
        await fetch(this.userApi, {
          method: "POST",
          body: JSON.stringify(this.userObj)
        }).then(response => response.text()).then(data => {
          console.log(data);
        });
      } catch (error) {
        console.log(error);
      }
    }
  },
  created() {
    this.getUsers();
  }
};
